<span class="line">      <span class="token keyword">const</span> pattern <span class="token operator">=</span> <span class="token template-string"><span class="token template-punctuation string">`</span><span class="token string">**/*.{</span><span class="token interpolation"><span class="token interpolation-punctuation punctuation">${</span>extensions<span class="token punctuation">.</span><span class="token function">join</span><span class="token punctuation">(</span><span class="token string">","</span><span class="token punctuation">)</span><span class="token interpolation-punctuation punctuation">}</span></span><span class="token string">}</span><span class="token template-punctuation string">`</span></span><span class="token punctuation">;</span></span>
